mapper.select:
SELECT COUNT(*) FROM PROJECT P JOIN (SELECT DISTINCT PNO, MNO FROM PMEMBER) M ON(P.PNO = M.PNO) WHERE P.P_STATUS = 'Y' AND P.PLEVEL = 0 AND M.MNO = #{scMno}
AND TO_DATE(P.PSTART_DATE) >= TO_DATE(#{startDate})
AND TO_DATE(P.PEND_DATE) <= TO_DATE(#{endDate})
AND P.PNAME LIKE '%' || #{projectName} || '%'
AND P.PPROGRESS = #{progress}

SELECT * FROM DEPT
WHERE CORP_NO = #{corpNo}
ORDER BY DNAME

mapper.update:
UPDATE TASK_REPLY
SET TSTATUS = 'N'
WHERE TASK_REPLY_NO = #{tno}

UPDATE ATTACHMENTS
   SET F_STATUS = 'N'
 WHERE PNO = #{pno}

UPDATE PROJECT
   SET P_STATUS = 'N'
 WHERE PNO = #{pno}

UPDATE PNOTICE
   SET PNOTICE_NAME = #{pnoticeName},
       PNOTICE_COTENT = #{pnoticeContent}
 WHERE PNOTICE_NO = #{pnoticeNo}

UPDATE ATTACHMENTS
   SET F_STATUS = 'N'
 WHERE PNOTICE_NO = #{pnoticeNo}

UPDATE PNOTICE
   SET PNOTICE_STATUS = 'N'
 WHERE PNOTICE_NO = #{nno}
